const Comment = ({ comment, last }) => {
  const date = new Date(comment.created + " GMT+00:00");
  console.log(last);
  return (
    <div key={comment.id} className="w-full flex flex-col py-1 px-3">
      <div>
        {comment.name[0].toUpperCase() + comment.name.slice(1)} on{" "}
        {date.toLocaleString(undefined, { month: "short", day: "numeric" })} at{" "}
        {date.getHours() > 12
          ? `${date.getHours() - 12}:${
              date.getMinutes() < 10
                ? "0" + date.getMinutes()
                : date.getMinutes()
            } PM`
          : `${date.getHours()}:${
              date.getMinutes() < 10
                ? "0" + date.getMinutes()
                : date.getMinutes()
            } AM`}
      </div>
      <div>{comment.message}</div>
      <div className="flex flex-row justify-center">
        {!last && <div className="h-[1px] bg-zinc-600 w-11/12" />}
      </div>
    </div>
  );
};

export default Comment;
