import {useState} from 'react'
import {  useAddComment } from '../hooks/useComments';

const InputForm = () => {
    const [message, setMessage] = useState("");
    const [name, setName] = useState("");
    const [error, setError] = useState({
        name: false,
        message: false
    })
    const { mutate } = useAddComment();

    return (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            if (name !== "" || message !== "") {
              mutate({
                name,
                message,
              });
              setMessage("");
              setName("");
            } else {
                setError({
                    name: name === '',
                    message: message === ''
                })
            }
          }}
          className='flex flex-col'
        >
          <input value={name} onChange={(e) => setName(e.target.value)} placeholder='Enter your name' className='text-zinc-900 outline-none p-2 mb-2 h-10'/>
          <textarea value={message} onChange={(e) => setMessage(e.target.value)} placeholder='Type your comment here...' className='text-zinc-900 resize-none outline-none p-2 h-40'/>
          <button type="submit">Comment</button>
        </form>

    );
  };

  export default InputForm
