const Comment = ({ comment, last }) => {
  const date = new Date(comment.created + " GMT+00:00");
  console.log(last);
  return (
    <div
      key={comment.id}
      className={
        last
          ? "w-full flex flex-col py-1 px-3"
          : "w-full flex flex-col py-1 px-3 border-b border-zinc-600"
      }
    >
      <div>
        {comment.name[0].toUpperCase() + comment.name.slice(1)} on{" "}
        {date.toLocaleString(undefined, { month: "short", day: "numeric" })} at{" "}
        {date.getHours() > 12
          ? `${date.getHours() - 12}:${
              date.getMinutes() < 10
                ? "0" + date.getMinutes()
                : date.getMinutes()
            } PM`
          : `${date.getHours()}:${
              date.getMinutes() < 10
                ? "0" + date.getMinutes()
                : date.getMinutes()
            } AM`}
      </div>
      <div>{comment.message}</div>
    </div>
  );
};

export default Comment;
