import {useState, useEffect, useContext} from 'react'
import { useComments, useAddComment, useDeleteComments } from '../hooks/useComments';
import { SocketContext } from '../SocketProvider';
import Comment from './Comment';
import InputForm from './InputForm';

const CommentFeed = () => {
    const [comments, setComments] = useState([]);
    const [message, setMessage] = useState("");
    const [name, setName] = useState("");
    const { data } = useComments();
    const { mutate } = useAddComment();
    const { mutate: deleteComments } = useDeleteComments();
    const socket = useContext(SocketContext);

    socket.on("connect", () => {
      console.log("You are connected with ID ", socket.id);
    });

    socket.on("receive-comment", (message) => {
      setComments([...comments, message]);
    });

    socket.on("clear-comments", () => setComments([]));

    useEffect(() => {
      if (data !== undefined) setComments(data);
    }, [data]);

    return (
      <div>
        <InputForm/>
        <button onClick={deleteComments}>Delete</button>
        {comments.map((c) => (
          <Comment comment={c} key={c.id} />
        ))}
      </div>
    );
  };

  export default CommentFeed
