import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { SocketProvider } from "./SocketProvider";
import CommentFeed from "./components/CommentFeed";
import InputForm from "./components/InputForm";
import OverflowMenu from "./components/OverflowMenu";
import { useState } from "react";

const queryClient = new QueryClient();

function App() {
  const [devTools, setDevTools] = useState(false);
  return (
    <SocketProvider>
      <QueryClientProvider client={queryClient}>
        <div className="flex flex-col items-center">
          <div className="flex flex-row justify-between w-full text-xl font-bold py-5">
            <span className="opacity-0 w-6" />
            Comments
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-5 h-5 mr-8 hover:stroke-zinc-400"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M16.5 10.5V6.75a4.5 4.5 0 1 0-9 0v3.75m-.75 11.25h10.5a2.25 2.25 0 0 0 2.25-2.25v-6.75a2.25 2.25 0 0 0-2.25-2.25H6.75a2.25 2.25 0 0 0-2.25 2.25v6.75a2.25 2.25 0 0 0 2.25 2.25Z"
              />
            </svg>
            {devTools && (
              <OverflowMenu
                items={[
                  {
                    name: "Delete comments",
                    action: () => console.log("deleting comments..."),
                  },
                ]}
                isOpen={devTools}
                toggle={setDevTools}
              />
            )}
          </div>
          <div className="sm:w-[500px] w-5/6">
            <InputForm />
            <CommentFeed />
          </div>
        </div>
      </QueryClientProvider>
    </SocketProvider>
  );
}
export default App;
