import {useState} from 'react'
import {  useAddComment } from '../hooks/useComments';

const InputForm = () => {
    const [message, setMessage] = useState("");
    const [name, setName] = useState("");
    const { mutate } = useAddComment();

    return (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            if (name !== "" && message !== "") {
              mutate({
                name,
                message,
              });
              setMessage("");
              setName("");
            }
          }}
          className='flex flex-col'
        >
          <label>Name</label>
          <input value={name} onChange={(e) => setName(e.target.value)} className='text-zinc-900'/>
          <label>Message</label>
          <textarea value={message} onChange={(e) => setMessage(e.target.value)} className='text-zinc-900 resize-none outline-none'/>
          <button type="submit">Comment</button>
        </form>

    );
  };

  export default InputForm
