import { useState, useEffect, useContext } from "react";
import { useComments } from "../hooks/useComments";
import { SocketContext } from "../SocketProvider";
import Comment from "./Comment";
import { useQueryClient } from "@tanstack/react-query";

const CommentFeed = () => {
  const [comments, setComments] = useState([]);
  const [newComments, setNewComments] = useState(0);
  const { data } = useComments();
  const queryClient = useQueryClient();

  const socket = useContext(SocketContext);

  socket.on("connect", () => {
    console.log("You are connected with ID ", socket.id);
  });

  socket.on("receive-comment", (message) => {
    queryClient.invalidateQueries(["comments"]);
    setNewComments(newComments + 1);

    setTimeout(() => {
      setNewComments(0);
    }, 10000);
  });

  socket.on("clear-comments", () =>
    queryClient.invalidateQueries(["comments"])
  );

  useEffect(() => {
    if (data !== undefined) setComments(data);
  }, [data]);

  return (
    <div
      className={
        comments.length > 0
          ? "border border-zinc-600 rounded-md max-h-[50vh] overflow-y-scroll"
          : ""
      }
    >
      {newComments > 0 && <div>{newComments} new comments</div>}
      {comments.map((c, i) => (
        <Comment comment={c} key={c.id} last={i === comments.length - 1} />
      ))}
    </div>
  );
};

export default CommentFeed;
