const Comment = ({comment}) => {
    const date = new Date(comment.created + ' GMT+00:00')
    return (
          <div key={comment.id} className='w-full border border-zinc-600 flex flex-col'>
            <div>{comment.name[0].toUpperCase() + comment.name.slice(1)} on {date.toLocaleString(undefined, { month: 'short', day: 'numeric' })} at {date.getHours() > 12 ? `${date.getHours() - 12}:${date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes()} PM`: `${date.getHours()}:${date.getMinutes() < 10 ? '0' + date.getMinutes() : date.getMinutes()} AM`}</div>
            <div>{comment.message}</div>
          </div>
    );
  };

  export default Comment
