import { useState } from "react";
import { useAddComment } from "../hooks/useComments";

const InputForm = () => {
  const [message, setMessage] = useState("");
  const [name, setName] = useState("");
  const [error, setError] = useState({
    name: false,
    message: false,
  });
  const { mutate } = useAddComment();
  console.log(error);
  return (
    <form
      onSubmit={(e) => {
        e.preventDefault();
        if (name !== "" && message !== "") {
          mutate({
            name,
            message,
          });
          setMessage("");
          setName("");
        } else {
          setError({
            name: name === "",
            message: message === "",
          });
        }
      }}
      className="flex flex-col"
    >
      <input
        value={name}
        onChange={(e) => {
          setName(e.target.value);
          setError({
            ...error,
            name: false,
          });
        }}
        placeholder="Enter your name"
        className={
          error.name
            ? "text-zinc-900 border-2 border-red-500 outline-none p-2 mb-2 h-10"
            : "text-zinc-900 outline-none p-2 mb-2 h-10"
        }
      />
      <textarea
        value={message}
        onChange={(e) => {
          setMessage(e.target.value);
          setError({
            ...error,
            message: false,
          });
        }}
        placeholder="Type your comment here..."
        className={
          error.message
            ? "text-zinc-900 border-2 border-red-500 outline-none resize-none p-2 mb-2 h-40"
            : "text-zinc-900 outline-none resize-none p-2 mb-2 h-40"
        }
      />
      <button type="submit" className="p-2">
        Comment
      </button>
    </form>
  );
};

export default InputForm;
